CREATE TABLE  users (id INT AUTO_INCREMENT NOT NULL, userFullName VARCHAR(250) , userEmail VARCHAR(255) NOT NULL, password VARCHAR(20) NOT NULL, phone VARCHAR(20) , CONSTRAINT PK_PK_User PRIMARY KEY(id));CREATE TABLE IF NOT EXISTS customers (id INT AUTO_INCREMENT NOT NULL, customerFullName VARCHAR(250) , customerEmail VARCHAR(255) NOT NULL, CONSTRAINT PK_PK_Customer PRIMARY KEY(id, customerFullName));